#Dashboard{color:#000;font-family:'Rubik'}.dashSection{border-radius:3px;border:1px solid #B9B9B9;background-color:#F0F0F0;margin:10px 0 10px}.dashSection h2{margin:10px 0 0 20px;border:none}.dashSection > p{margin:0 20px}.boxes{display:grid;grid-template:minmax(170px,max-content) / repeat(4,1fr);grid-gap:20px;padding:15px;width:calc(100% - 30px)}@media screen and (max-width:1024px){.boxes{grid-template:minmax(170px,max-content) / repeat(3,1fr)}}.dash-box{display:flex;flex-direction:column;text-align:center;background-color:white;color:#222222;padding:10px;-webkit-box-shadow:2px 2px 5px 0 rgba(128,128,128,1);-moz-box-shadow:2px 2px 5px 0 rgba(128,128,128,1);box-shadow:2px 2px 5px 0 rgba(128,128,128,1);border-radius:5px}.dashHeader{font-size:20px;font-weight:bold;text-align:center;margin:7px 0 0}.dash-box > .top > b:first-child{font-family:'Rubik';font-size:.8em;text-transform:uppercase}.dash-box > .top > b:first-child + .dashHeader{margin:0}#dashboardSelf-profile .dashHeader{display:flex;justify-content:center;align-items:center}.dash-box .wds-avatar{margin-right:.5em;max-width:1.5em;max-height:1.5em}.dash-box .wds-avatar__image{border:2px solid #00d6d6}.dash-box > footer{display:flex;align-items:center;padding-top:10px;margin-top:auto}.dash-box > footer > .wds-button:not(.wds-is-secondary){margin-left:auto}.dash-box > footer > .wds-button.wds-is-text{color:#5f7a7b !important}.dash-box > footer > .wds-button.wds-is-secondary:first-child{margin-left:0;padding:7px}.dash-box > footer .wds-dropdown{margin-bottom:-7px}.dash-box > footer .wds-dropdown__content{width:200px;padding:5px}#Dashboard a,#Dashboard a:visited,#Dashboard a:active{color:inherit}.dash-box[data-type="contribute"] .top,.dash-box[data-type="contribute"] .dashHeader,.dash-box[data-type="contribute"] p{text-align:left}.wds-global-navigation__user-menu a[data-tracking-label="Dashboard"] > div{display:flex;justify-content:space-between;width:100%}.dashboardDialog{font-family:'Rubik';color:#000;z-index:5}.dashboardDialog .wds-dialog__actions{display:flex;align-items:center}.dashboardDialog .wds-dialog__actions > .wds-button{font-family:'Rubik';text-transform:uppercase;font-size:12px;font-weight:bold;color:#5f7a7b}.dashboardDialog .wds-dialog__actions > .wds-button:not(.wds-is-text){color:#000}.dashboardDialog .wds-dialog__actions > .wds-button:not(.wds-is-text):last-child{margin:0 10px}.user-anon #globalNavigation .wds-avatar{animation:pulse 4s infinite;animation-timing-function:linear}@keyframes pulse{20%{transform:scale(1.5)}40%{transform:scale(1) }}@media only screen and (max-width:1023px){#globalNavigation .wds-global-navigation__user-menu a[data-tracking-label="Dashboard"] + .wds-dropdown-level-2__content{left:initial;right:100%}}

